import React,{useState} from 'react';
import {Link,Redirect} from 'react-router-dom';
import {Box, Grommet,Button,Heading} from 'grommet';
import './App.css';
import Appheader from './component/Appheader';

function App() {
  const [login,setLogin]=useState(false);
  const [sign,setSign]=useState(false);
  if(login){
    return <Redirect to="/home" push={true} />

  }
  else if(sign){
    return <Redirect to="/sign" push={true} />
  }
  else{
    return (


      <Box fill={true}    background={{color:"light-1"}}  >
        <Appheader />

        <Box  direction="row" align="stretch" justify="between" width="xxlarge" margin={{"top":"5%"}} >
          <Box pad="large" width="50%">
            <Heading margin="none" level="2" color="black">Build an application  powered by</Heading><Heading margin="none" level="2" color="blue"> Machine Learning</Heading>

          </Box>
          <div className="image">
          <Box width="50%">
            <img src="/s1.png" ></img>
          </Box>
          </div>
        </Box>
      </Box>


    );
  }

}

export default App;
