import React,{useState} from 'react';
import {Link,Redirect} from 'react-router-dom';
import {Box, Form,FormField,TextInput,Button,Anchor,RadioButtonGroup} from 'grommet';

import '../App.css';
  

function Sign() {
    const [name,setname]=useState('');
    const [email,setemail]=useState('');
    const [paswd,setpaswd]=useState('');
    const [conpaswd,setconpaswd]=useState('');
    const [gen,setgen]=useState('');
    const [dob,setdob]=useState('');
    console.log(name,email,paswd,conpaswd,gen,dob);
    return (
        <Box fill={true}   justify="center"  align="center" background={{color:"light-1"}}>
            <Box background={{color:"light-2"}} justify="center" round="small" pad="large" margin={{top:"medium",left:"medium",right:"medium"}} >
                
                    <Form  onSubmit={(event)=>console.log(event.value)} >
                        <FormField label="Name" name="name">
                            <TextInput name="name" value={name} onChange={event=> setname(event.target.value)} />
                        </FormField>
                        <FormField label="Email" name="email" >
                            <TextInput name="email" value={email} onChange={event=> setemail(event.target.value)}/>
                        </FormField>
                        <FormField label="Password" name="paswd" >
                            <TextInput type="password" name="paswd" value={paswd} onChange={event=> setpaswd(event.target.value)}/>
                        </FormField>
                        <FormField label="Confirm Password" name="conpaswd" >
                            <TextInput type="password" name="conpaswd" value={conpaswd} onChange={event=> setconpaswd(event.target.value)}/>
                        </FormField>
                        <FormField label="Gender" name="gen">
                            <RadioButtonGroup name="gen" options={["Male","Female"]} value={gen} onChange={event=> setgen(event.target.value)}/>

                        </FormField>
                        <FormField label="Date Of Birth" name="dob">
                            <TextInput type="calender" name="dob" value={dob} onChange={event=> setdob(event.target.value)} />
                        </FormField>
                        <Box direction="column" justify="center" gap="small" margin={{top:"medium"}} >
                            <Button size="medium" fill={false} type="submit" label="Sign-in" primary color="brand" alignSelf="center" />
                                

                        </Box>
                    </Form>
                

            </Box>
            
        </Box>
    )
}

export default Sign;
