import React,{useState} from 'react';
import {Link,Redirect} from 'react-router-dom';
import {Box, Grommet,Button,Heading,Text,Grid} from 'grommet';
import '../App.css';
import Aisub from './Aisub';
import Comp from '../photo/comp.png';
import Ss from '../photo/s2.png'; 
import St from '../photo/s3.png';
import Sf from '../photo/s4.png';
const arobj =[{
    image: Comp,
    head: "Enterprise AI and data platform solutions",
    text: "Dummy text is also used to demonstrate the appearance of different typefaces and layouts, and in general the content of dummy text is nonsensical. used to demonstrate the appearance of different typefaces and layouts, and in general the content of dummy text is nonsensical"
},
{
    image: Ss,
    head: "",
    text: ""
},
{
    image: St,
    head: "",
    text: ""
},
{
    image: Sf,
    head: "",
    text: ""
}];
function Ai() {
    const [image,setimage]=useState(Comp);
    const [head,sethead]= useState("Enterprise AI and data platform solutions");
    const [text,settext]=useState("Dummy text is also used to demonstrate the appearance of different typefaces and layouts, and in general the content of dummy text is nonsensical. used to demonstrate the appearance of different typefaces and layouts, and in general the content of dummy text is nonsensical");
    

    return (
        <>
            <Box height={{max:"700px",min:"auto"}} width="xxlarge" background="white" pad="large" gap="medium" direction="column" justify="center"  >
                <Box width="100%"  direction="row-responsive" pad="large" align="center" >
                    <Box width="233px" height={{max:"94px",min:"94px"}} onClick={()=> {setimage(Comp); sethead("Enterprise AI and data platform solutions"); settext("Dummy text is also used to demonstrate the appearance of different typefaces and layouts, and in general the content of dummy text is nonsensical. used to demonstrate the appearance of different typefaces and layouts, and in general the content of dummy text is nonsensical");}} background="#1AA15F" justify="center" align="center">
                    <Heading margin="none" level="5" color="white">Research & Solutions </Heading>

                    </Box>
                    <Box width="233px" height={{max:"94px",min:"94px"}} onClick={()=> {setimage(Ss); sethead("Design and development use latest technology"); settext("And in general the content of dummy text is nonsensical. used to demonstrate the appearance of different typefaces and layouts, and in general the content of dummy text is nonsensical. Dummy text is also used to demonstrate the appearance of different typefaces and layouts");}} background="white" justify="center" align="center">
                    <Heading margin="none" level="5" color="black">Design & Strategy </Heading>

                    </Box>
                    <Box width="233px" height={{max:"94px",min:"94px"}} background="white" justify="center" onClick={()=> {setimage(St); sethead("Artificial intelligence use in automobile industry"); settext("Used to demonstrate the appearance of different typefaces and layouts, and in general the content of dummy text is nonsensical. text is also used to demonstrate the appearance of different typefaces and layouts, and in general the content of dummy text is nonsensical");}} align="center">
                    <Heading margin="none" level="5" color="black">Artificial Intelligence </Heading>

                    </Box>
                    <Box width="233px" height={{max:"94px",min:"94px"}} background="white" justify="center" onClick={()=> {setimage(Sf); sethead("machine learning platform used cloud deployment"); settext("Dummy text is also used. used to demonstrate the appearance of different typefaces and layouts, and in general the content of dummy text is nonsensica to demonstrate the appearance of different typefaces and layouts, and in general the content of dummy text is nonsensical.");}} align="center">
                    <Heading margin="none" level="5" color="black">Cloud Deployment </Heading>

                    </Box>
                    




                </Box>
                <Box width="100%">
                    <Aisub image={arobj[0].image} head={arobj[0].head} text={arobj[0].text} ></Aisub>
                </Box>
                
            </Box>
        </>
    )
}
export default Ai;
